package demo;

public class pyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<=5 ;i++)  // i is used for row elements  
		{  
		for(int j=0; j<i; j++)  // j is used for column elements  
		{  
		System.out.print(j+1);  
		}  
		System.out.println();  
		}  

	}

}
